"use client";

import { useState, useEffect } from "react";
import clsx from "clsx";
import Link from "next/link";

type DateRange = "Week" | "Month" | "Quarter" | "Year";

interface AnalyticsData {
    totalVolume: number;
    successRate: number;
    avgTicket: number;
    successCount: number;
    failedCount: number;
    pendingCount: number;
    topStores: { id: string; name: string; volume: number; count: number; trend: number }[];
    weeklyBars: { day: string; storeA: number; storeB: number }[];
}

const DAY_LABELS = ["MON", "TUE", "WED", "THU", "FRI", "SAT", "SUN"];

export default function AnalyticsPage() {
    const [range, setRange] = useState<DateRange>("Week");
    const [data, setData] = useState<AnalyticsData | null>(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        setLoading(true);
        fetch(`/api/analytics?range=${range}`)
            .then((r) => r.json())
            .then((d) => { setData(d); setLoading(false); })
            .catch(() => setLoading(false));
    }, [range]);

    const formatCurrency = (cents: number) =>
        `R ${(cents / 100).toLocaleString("en-ZA", { minimumFractionDigits: 0, maximumFractionDigits: 0 })}`;

    const barMax = data
        ? Math.max(...data.weeklyBars.map((b) => Math.max(b.storeA, b.storeB)), 1)
        : 1;

    const totalHealth = (data?.successCount ?? 0) + (data?.failedCount ?? 0) + (data?.pendingCount ?? 0);
    const successPct = totalHealth > 0 ? (data!.successCount / totalHealth) * 100 : 98;
    const failedPct = totalHealth > 0 ? (data!.failedCount / totalHealth) * 100 : 1.5;

    // SVG donut values (circle circumference ≈ 100.5 for r=16)
    const C = 100.5;
    const successDash = `${(successPct / 100) * C}, ${C}`;
    const failedOffset = -(successPct / 100) * C;
    const failedDash = `${(failedPct / 100) * C}, ${C}`;

    return (
        <div className="flex flex-col min-h-full pb-24">
            {/* Mobile Header */}
            <header className="sticky top-0 z-20 flex md:hidden flex-col bg-background-light/80 dark:bg-background-dark/80 backdrop-blur-md border-b border-slate-200 dark:border-slate-800 px-4 pt-8 pb-4 gap-4">
                <div className="flex items-center justify-between">
                    <div>
                        <p className="text-xs font-semibold text-primary uppercase tracking-wider">Payment Switch</p>
                        <h1 className="text-2xl font-bold tracking-tight">Analytics</h1>
                    </div>
                    <button className="p-2 rounded-full bg-slate-200 dark:bg-slate-800 text-slate-600 dark:text-slate-400 hover:bg-slate-300 dark:hover:bg-slate-700 transition-colors">
                        <span className="material-symbols-outlined">filter_list</span>
                    </button>
                </div>
                {/* Date Tabs */}
                <div className="flex bg-slate-200/50 dark:bg-slate-800/50 p-1 rounded-lg">
                    {(["Week", "Month", "Quarter", "Year"] as DateRange[]).map((r) => (
                        <button
                            key={r}
                            onClick={() => setRange(r)}
                            className={clsx(
                                "flex-1 py-1.5 text-sm font-medium rounded-md transition-all",
                                range === r
                                    ? "bg-white dark:bg-primary text-slate-900 dark:text-white shadow-sm"
                                    : "text-slate-500 dark:text-slate-400 hover:text-slate-700 dark:hover:text-slate-300"
                            )}
                        >
                            {r}
                        </button>
                    ))}
                </div>
            </header>

            {/* Desktop Header */}
            <div className="hidden md:flex items-center justify-between mb-6">
                <div>
                    <div className="flex items-center gap-2">
                        <h2 className="text-2xl font-bold text-slate-900 dark:text-white">Sales & Analytics</h2>
                    </div>
                    <p className="text-slate-500 dark:text-slate-400">Performance overview across all stores</p>
                </div>
                <div className="flex items-center gap-3">
                    {/* Desktop Date Tabs */}
                    <div className="flex bg-slate-200/50 dark:bg-slate-800/50 p-1 rounded-lg">
                        {(["Week", "Month", "Quarter", "Year"] as DateRange[]).map((r) => (
                            <button
                                key={r}
                                onClick={() => setRange(r)}
                                className={clsx(
                                    "px-4 py-1.5 text-sm font-medium rounded-md transition-all",
                                    range === r
                                        ? "bg-white dark:bg-primary text-slate-900 dark:text-white shadow-sm"
                                        : "text-slate-500 dark:text-slate-400 hover:text-slate-700"
                                )}
                            >
                                {r}
                            </button>
                        ))}
                    </div>
                    <button className="flex items-center gap-2 px-4 py-2 bg-white dark:bg-slate-800 border border-slate-200 dark:border-slate-700 rounded-lg text-slate-700 dark:text-slate-300 font-semibold text-sm hover:bg-slate-50 dark:hover:bg-slate-700/50 transition-colors">
                        <span className="material-symbols-outlined text-[20px]">download</span>
                        Export
                    </button>
                </div>
            </div>

            <main className="px-4 md:px-0 py-4 md:py-0 space-y-6">
                {/* Key Metrics Scroll Row */}
                <section className="flex gap-4 overflow-x-auto hide-scrollbar -mx-4 px-4 md:mx-0 md:px-0 md:grid md:grid-cols-3">
                    {[
                        {
                            label: "Total Volume",
                            value: loading ? "—" : formatCurrency(data?.totalVolume ?? 0),
                            trend: 12.4,
                            up: true
                        },
                        {
                            label: "Success Rate",
                            value: loading ? "—" : `${(data?.successRate ?? 98.2).toFixed(1)}%`,
                            trend: 0.2,
                            up: false
                        },
                        {
                            label: "Avg. Ticket",
                            value: loading ? "—" : formatCurrency(data?.avgTicket ?? 0),
                            trend: 4.1,
                            up: true
                        }
                    ].map((metric) => (
                        <div key={metric.label} className="flex-none w-40 md:w-auto p-4 rounded-xl bg-white dark:bg-slate-800 border border-slate-200 dark:border-slate-700 shadow-sm">
                            <p className="text-xs text-slate-500 dark:text-slate-400 font-medium mb-1">{metric.label}</p>
                            <h3 className="text-xl font-bold">{metric.value}</h3>
                            <div className={clsx("flex items-center mt-2 text-xs font-bold", metric.up ? "text-emerald-500" : "text-rose-500")}>
                                <span className="material-symbols-outlined text-sm mr-1">
                                    {metric.up ? "trending_up" : "trending_down"}
                                </span>
                                {metric.trend}%
                            </div>
                        </div>
                    ))}
                </section>

                {/* Store Comparison Bar Chart */}
                <section className="space-y-4">
                    <div className="flex items-center justify-between">
                        <h2 className="text-lg font-bold">Store Comparison</h2>
                        <div className="flex gap-3">
                            <span className="flex items-center text-[10px] font-bold uppercase tracking-tighter text-primary">
                                <span className="w-2 h-2 rounded-full bg-primary mr-1"></span> Primary
                            </span>
                            <span className="flex items-center text-[10px] font-bold uppercase tracking-tighter text-slate-400">
                                <span className="w-2 h-2 rounded-full bg-slate-400 mr-1"></span> Others
                            </span>
                        </div>
                    </div>
                    <div className="w-full bg-white dark:bg-slate-800 rounded-xl p-4 border border-slate-200 dark:border-slate-700">
                        <div className="flex items-end justify-between h-32 gap-1">
                            {loading
                                ? DAY_LABELS.map((d, i) => (
                                    <div key={d} className="flex-1 flex flex-col justify-end gap-1 h-full">
                                        <div className="w-full bg-slate-200 dark:bg-slate-700 rounded-t animate-pulse" style={{ height: `${(i % 3 + 1) * 20 + 20}%` }} />
                                        <div className="w-full bg-slate-100 dark:bg-slate-600 rounded-t animate-pulse" style={{ height: `${(i % 2 + 1) * 15 + 10}%` }} />
                                    </div>
                                ))
                                : (data?.weeklyBars ?? []).map((bar, i) => {
                                    const aH = barMax > 0 ? (bar.storeA / barMax) * 100 : 0;
                                    const bH = barMax > 0 ? (bar.storeB / barMax) * 100 : 0;
                                    return (
                                        <div key={i} className="flex-1 flex flex-col justify-end gap-1 h-full group">
                                            <div className="w-full bg-slate-400/20 group-hover:bg-slate-400/40 rounded-t transition-all" style={{ height: `${Math.max(bH, 4)}%` }} />
                                            <div className="w-full bg-primary group-hover:bg-primary/80 rounded-t transition-all" style={{ height: `${Math.max(aH, 4)}%` }} />
                                        </div>
                                    );
                                })}
                        </div>
                        <div className="flex justify-between text-[10px] text-slate-400 font-medium pt-2 border-t border-slate-100 dark:border-slate-700">
                            {DAY_LABELS.map((d) => <span key={d}>{d}</span>)}
                        </div>
                    </div>
                </section>

                {/* Transaction Health + Top Stores Grid */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    {/* Transaction Health Donut */}
                    <section className="bg-white dark:bg-slate-800 rounded-xl p-5 border border-slate-200 dark:border-slate-700">
                        <h2 className="text-sm font-bold text-slate-500 dark:text-slate-400 mb-4 uppercase">Transaction Health</h2>
                        <div className="flex items-center gap-6">
                            <div className="relative flex items-center justify-center w-24 h-24 shrink-0">
                                <svg className="w-full h-full -rotate-90" viewBox="0 0 36 36">
                                    {/* Track */}
                                    <circle className="stroke-slate-200 dark:stroke-slate-700" cx="18" cy="18" fill="none" r="16" strokeWidth="3" />
                                    {/* Success */}
                                    <circle
                                        className="stroke-primary transition-all duration-700"
                                        cx="18" cy="18" fill="none" r="16"
                                        strokeWidth="3"
                                        strokeDasharray={successDash}
                                    />
                                    {/* Failed */}
                                    <circle
                                        className="stroke-rose-500 transition-all duration-700"
                                        cx="18" cy="18" fill="none" r="16"
                                        strokeWidth="3"
                                        strokeDasharray={failedDash}
                                        strokeDashoffset={failedOffset}
                                    />
                                </svg>
                                <div className="absolute inset-0 flex flex-col items-center justify-center">
                                    <span className="text-lg font-bold">{loading ? "—" : `${successPct.toFixed(0)}%`}</span>
                                </div>
                            </div>
                            <div className="flex-1 space-y-2">
                                <div className="flex items-center justify-between">
                                    <div className="flex items-center gap-2">
                                        <div className="w-2 h-2 rounded-full bg-primary" />
                                        <span className="text-xs font-medium">Successful</span>
                                    </div>
                                    <span className="text-xs font-bold">{loading ? "—" : (data?.successCount ?? 0).toLocaleString()}</span>
                                </div>
                                <div className="flex items-center justify-between">
                                    <div className="flex items-center gap-2">
                                        <div className="w-2 h-2 rounded-full bg-rose-500" />
                                        <span className="text-xs font-medium">Failed</span>
                                    </div>
                                    <span className="text-xs font-bold">{loading ? "—" : (data?.failedCount ?? 0).toLocaleString()}</span>
                                </div>
                                <div className="flex items-center justify-between">
                                    <div className="flex items-center gap-2">
                                        <div className="w-2 h-2 rounded-full bg-amber-500" />
                                        <span className="text-xs font-medium">Pending</span>
                                    </div>
                                    <span className="text-xs font-bold">{loading ? "—" : (data?.pendingCount ?? 0).toLocaleString()}</span>
                                </div>
                            </div>
                        </div>
                    </section>

                    {/* Top Performing Stores */}
                    <section className="space-y-4">
                        <div className="flex items-center justify-between">
                            <h2 className="text-lg font-bold">Top Performing Units</h2>
                            <Link href="/stores" className="text-xs font-semibold text-primary hover:underline">View All</Link>
                        </div>
                        <div className="space-y-3">
                            {loading ? (
                                [1, 2, 3].map((i) => (
                                    <div key={i} className="flex items-center p-3 bg-white dark:bg-slate-800 rounded-xl border border-slate-200 dark:border-slate-700 animate-pulse">
                                        <div className="w-10 h-10 rounded-lg bg-slate-200 dark:bg-slate-700" />
                                        <div className="ml-3 flex-1 space-y-2">
                                            <div className="h-3 bg-slate-200 dark:bg-slate-700 rounded w-3/4" />
                                            <div className="h-2 bg-slate-200 dark:bg-slate-700 rounded w-1/2" />
                                        </div>
                                    </div>
                                ))
                            ) : (data?.topStores ?? []).slice(0, 3).map((store, i) => (
                                <div key={store.id} className="flex items-center p-3 bg-white dark:bg-slate-800 rounded-xl border border-slate-200 dark:border-slate-700 hover:shadow-md transition-shadow">
                                    <div className={clsx(
                                        "w-10 h-10 rounded-lg flex items-center justify-center font-bold text-sm",
                                        i === 0 ? "bg-primary/10 text-primary" : "bg-slate-200 dark:bg-slate-700 text-slate-500"
                                    )}>
                                        {i + 1}
                                    </div>
                                    <div className="ml-3 flex-1 min-w-0">
                                        <p className="text-sm font-bold truncate">{store.name}</p>
                                        <p className="text-[10px] text-slate-500">{store.count.toLocaleString()} Trans • Yoco Routing</p>
                                    </div>
                                    <div className="text-right">
                                        <p className="text-sm font-bold">{formatCurrency(store.volume)}</p>
                                        <p className={clsx("text-[10px] font-medium", store.trend >= 0 ? "text-emerald-500" : "text-rose-500")}>
                                            {store.trend >= 0 ? "+" : ""}{store.trend.toFixed(1)}%
                                        </p>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </section>
                </div>

                {/* Gateway Status Banner */}
                <section className="flex items-center justify-between p-4 bg-primary/5 dark:bg-primary/10 border border-primary/20 rounded-xl">
                    <div className="flex items-center gap-3">
                        <div className="relative flex h-3 w-3">
                            <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-emerald-400 opacity-75"></span>
                            <span className="relative inline-flex rounded-full h-3 w-3 bg-emerald-500"></span>
                        </div>
                        <div>
                            <p className="text-xs font-bold text-primary tracking-wide">GATEWAY STATUS</p>
                            <p className="text-[10px] font-medium opacity-70">Connected to Yoco Switch Primary</p>
                        </div>
                    </div>
                    <span className="material-symbols-outlined text-primary text-xl">cloud_done</span>
                </section>
            </main>
        </div>
    );
}

import type { Metadata } from "next";
import { Inter } from "next/font/google";
import "./globals.css";
import { AppShell } from "@/components/layout/AppShell";

const inter = Inter({
  variable: "--font-inter",
  subsets: ["latin"],
  display: "swap",
});

export const metadata: Metadata = {
  title: "UrbanSmart Payment Switch",
  description: "Payment Switch Dashboard",
};

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  return (
    <html lang="en">
      <head>
        <link
          href="https://fonts.googleapis.com/css2?family=Material+Symbols+Outlined:wght,FILL@100..700,0..1&display=swap"
          rel="stylesheet"
        />
      </head>
      <body className={`${inter.variable} antialiased bg-background text-foreground`}>
        <AppShell>
          {children}
        </AppShell>
      </body>
    </html>
  );
}

"use client";

import { useState, useEffect } from "react";
import clsx from "clsx";
import Link from "next/link";

type ViewTab = "Overview" | "Issues" | "History";
type DiscrepancyType = "MISMATCH" | "NOT_FOUND" | "MATCHED";

interface DiscrepancyItem {
    id: string;
    chargeId: string;
    date: string;
    type: DiscrepancyType;
    platformAmount: number | null;
    yocoAmount: number | null;
    note: string;
    resolved: boolean;
}

// Static reconciliation items (these represent cross-system comparison data)
const STATIC_ITEMS: DiscrepancyItem[] = [
    { id: "1", chargeId: "CH_0X92K182L", date: "Oct 24, 2023 • 14:22", type: "MISMATCH", platformAmount: 145000, yocoAmount: 120000, note: "Possible partial refund not recorded locally.", resolved: false },
    { id: "2", chargeId: "YOCO_M82L9P", date: "Oct 24, 2023 • 15:01", type: "NOT_FOUND", platformAmount: null, yocoAmount: 49900, note: "External transaction detected.", resolved: false },
    { id: "3", chargeId: "CH_P832MM1", date: "Oct 24, 2023 • 13:45", type: "MATCHED", platformAmount: 82000, yocoAmount: 82000, note: "Amounts match.", resolved: true },
    { id: "4", chargeId: "CH_8XP29Q4R", date: "Oct 23, 2023 • 09:15", type: "MISMATCH", platformAmount: 220000, yocoAmount: 185000, note: "Potential fee discrepancy on refund.", resolved: false },
    { id: "5", chargeId: "YOCO_K901AS", date: "Oct 23, 2023 • 11:30", type: "NOT_FOUND", platformAmount: null, yocoAmount: 67500, note: "Yoco charge has no local record.", resolved: false },
];

const DRIFT_BARS = [
    { h: "80%", color: "bg-primary" },
    { h: "60%", color: "bg-primary/40" },
    { h: "30%", color: "bg-rose-500" },
    { h: "90%", color: "bg-primary" },
    { h: "45%", color: "bg-primary/60" },
    { h: "70%", color: "bg-primary/80" },
    { h: "55%", color: "bg-primary/50" },
];

const fmt = (cents: number) => `R ${(cents / 100).toLocaleString("en-ZA", { minimumFractionDigits: 2 })}`;

export default function ReconciliationPage() {
    const [activeTab, setActiveTab] = useState<ViewTab>("Issues");
    const [items, setItems] = useState<DiscrepancyItem[]>(STATIC_ITEMS);
    const [syncing, setSyncing] = useState(false);
    const [lastSync, setLastSync] = useState("2m ago");
    const [dbStats, setDbStats] = useState({ total: 0, discrepancies: 0 });
    const [loading, setLoading] = useState(true);

    const loadData = () => {
        fetch("/api/reconciliation")
            .then((r) => r.json())
            .then((data) => {
                if (data.totalVolume !== undefined) {
                    setDbStats({ total: data.totalVolume, discrepancies: data.discrepancyCount });
                }
                if (Array.isArray(data.items) && data.items.length > 0) {
                    setItems(data.items);
                }
            })
            .catch(() => { })
            .finally(() => setLoading(false));
    };

    useEffect(() => {
        // eslint-disable-next-line react-hooks/set-state-in-effect
        setLoading(true);
        loadData();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const handleSync = async () => {
        setSyncing(true);
        await new Promise((r) => setTimeout(r, 1000));
        loadData();
        setSyncing(false);
        setLastSync("just now");
    };

    const handleFix = (id: string) => {
        setItems((prev) => prev.map((item) => item.id === id ? { ...item, resolved: true, type: "MATCHED" } : item));
    };

    const handleImport = (id: string) => {
        setItems((prev) => prev.map((item) => item.id === id ? { ...item, resolved: true, type: "MATCHED", platformAmount: item.yocoAmount } : item));
    };

    const historyItems = items.filter((i) => i.resolved);
    const displayItems = activeTab === "Issues" ? items.filter((i) => !i.resolved) : activeTab === "History" ? historyItems : items;

    return (
        <div className="flex flex-col min-h-full bg-background-dark text-slate-100">
            {/* Mobile Header */}
            <header className="sticky top-0 z-20 flex md:hidden items-center justify-between bg-background-dark/80 backdrop-blur-md border-b border-slate-700/50 p-4">
                <div className="flex items-center gap-3">
                    <Link href="/" className="flex size-10 items-center justify-center rounded-lg bg-slate-800 text-slate-100">
                        <span className="material-symbols-outlined">arrow_back_ios_new</span>
                    </Link>
                    <div>
                        <h1 className="text-lg font-bold leading-tight">Ledger Tool</h1>
                        <p className="text-xs text-slate-400 font-medium">Store: Main Terminal 01</p>
                    </div>
                </div>
                <div className="flex gap-2">
                    <button className="flex size-10 items-center justify-center rounded-lg bg-slate-800 text-slate-100 hover:bg-slate-700 transition-colors">
                        <span className="material-symbols-outlined">search</span>
                    </button>
                    <button className="flex size-10 items-center justify-center rounded-lg bg-slate-800 text-slate-100 hover:bg-slate-700 transition-colors">
                        <span className="material-symbols-outlined">filter_list</span>
                    </button>
                </div>
            </header>

            {/* Desktop Header */}
            <div className="hidden md:flex items-center justify-between mb-6">
                <div>
                    <h2 className="text-2xl font-bold text-white">Reconciliation Tool</h2>
                    <p className="text-slate-400">Compare platform ledger vs. Yoco API data</p>
                </div>
                <div className="flex gap-2">
                    <button
                        onClick={handleSync}
                        disabled={syncing}
                        className="flex items-center gap-2 px-4 py-2 bg-primary text-white rounded-lg text-sm font-semibold hover:opacity-90 disabled:opacity-60 transition-all shadow-lg shadow-primary/20"
                    >
                        <span className={clsx("material-symbols-outlined text-[18px]", syncing && "animate-spin")}>
                            {syncing ? "progress_activity" : "sync"}
                        </span>
                        {syncing ? "Syncing..." : "Manual Sync"}
                    </button>
                    <button className="flex items-center gap-2 px-4 py-2 bg-slate-800 text-slate-200 border border-slate-700 rounded-lg text-sm font-semibold hover:bg-slate-700 transition-colors">
                        <span className="material-symbols-outlined text-[18px]">download</span>
                        Export
                    </button>
                </div>
            </div>

            <main className="flex-1 pb-8 space-y-4">
                {/* Batch Summary Card */}
                <section className="px-4 md:px-0">
                    <div className="rounded-xl bg-slate-800/60 p-5 border border-slate-700/50 relative overflow-hidden group">
                        <div className="absolute top-0 right-0 p-3 opacity-10 group-hover:opacity-20 transition-opacity pointer-events-none">
                            <span className="material-symbols-outlined text-6xl">receipt_long</span>
                        </div>
                        <div className="relative z-10">
                            <div className="flex items-center justify-between mb-4">
                                <span className="px-2 py-1 bg-primary/20 text-primary text-[10px] font-bold uppercase tracking-wider rounded">
                                    Batch #8829-Yoco
                                </span>
                                <span className="text-slate-400 text-xs">Updated {lastSync}</span>
                            </div>
                            <div className="grid grid-cols-2 gap-4">
                                <div>
                                    <p className="text-slate-400 text-xs font-medium uppercase tracking-wide">Total Volume</p>
                                    <p className="text-2xl font-bold text-slate-100">
                                        {dbStats.total > 0 ? fmt(dbStats.total) : "R 142,500"}
                                    </p>
                                </div>
                                <div>
                                    <p className="text-rose-400 text-xs font-medium uppercase tracking-wide">Discrepancies</p>
                                    <p className="text-2xl font-bold text-rose-400">
                                        {dbStats.discrepancies > 0 ? `${dbStats.discrepancies} Items` : `${items.filter((i) => !i.resolved).length} Items`}
                                    </p>
                                </div>
                            </div>
                            <div className="mt-4 flex items-center gap-3">
                                <button
                                    onClick={handleSync}
                                    disabled={syncing}
                                    className="flex-1 flex items-center justify-center gap-2 rounded-lg bg-primary py-2.5 text-sm font-bold text-white shadow-lg shadow-primary/20 hover:bg-primary/90 disabled:opacity-60 transition-all active:scale-95"
                                >
                                    <span className={clsx("material-symbols-outlined text-sm", syncing && "animate-spin")}>
                                        {syncing ? "progress_activity" : "sync"}
                                    </span>
                                    {syncing ? "Syncing..." : "Manual Sync"}
                                </button>
                                <button className="size-10 flex items-center justify-center rounded-lg bg-slate-700 text-slate-300 hover:text-white transition-colors">
                                    <span className="material-symbols-outlined">download</span>
                                </button>
                            </div>
                        </div>
                    </div>
                </section>

                {/* Segmented Control */}
                <div className="px-4 md:px-0 sticky top-[73px] md:top-0 z-10 bg-background-dark/95 backdrop-blur-sm py-2">
                    <div className="flex h-11 items-center justify-center rounded-xl bg-slate-800 p-1 border border-slate-700">
                        {(["Overview", "Issues", "History"] as ViewTab[]).map((tab) => (
                            <button
                                key={tab}
                                onClick={() => setActiveTab(tab)}
                                className={clsx(
                                    "flex h-full flex-1 items-center justify-center rounded-lg text-sm font-semibold transition-all",
                                    activeTab === tab
                                        ? "bg-background-dark text-primary"
                                        : "text-slate-400 hover:text-slate-200"
                                )}
                            >
                                {tab}
                                {tab === "Issues" && items.filter((i) => !i.resolved).length > 0 && (
                                    <span className="ml-1.5 bg-rose-500 text-white text-[9px] font-bold rounded-full w-4 h-4 flex items-center justify-center">
                                        {items.filter((i) => !i.resolved).length}
                                    </span>
                                )}
                            </button>
                        ))}
                    </div>
                </div>

                {/* Items List */}
                {activeTab !== "Overview" && (
                    <div className="px-4 md:px-0 space-y-3">
                        <div className="flex items-center justify-between">
                            <h3 className="text-sm font-bold uppercase tracking-widest text-slate-300">
                                {activeTab === "Issues" ? "Unresolved Items" : "Resolved Items"}
                            </h3>
                            <span className="text-slate-500 text-xs font-medium">Sorted by: Amount (High-Low)</span>
                        </div>
                        {displayItems.length === 0 && (
                            <div className="text-center py-12 text-slate-400">
                                <span className="material-symbols-outlined text-4xl block mb-2 text-emerald-500">check_circle</span>
                                <p className="font-semibold text-emerald-400">All items reconciled!</p>
                            </div>
                        )}
                        {displayItems.map((item) => {
                            const isResolved = item.type === "MATCHED";
                            return (
                                <div key={item.id} className={clsx(
                                    "rounded-xl border p-4 space-y-3",
                                    isResolved
                                        ? "border-slate-700/40 bg-slate-800/20 opacity-60"
                                        : "border-rose-500/30 bg-rose-500/5"
                                )}>
                                    <div className="flex justify-between items-start">
                                        <div>
                                            <p className="text-xs font-mono text-slate-400">ID: {item.chargeId}</p>
                                            <p className="text-sm font-bold text-slate-100">{item.date}</p>
                                        </div>
                                        <span className={clsx(
                                            "flex items-center gap-1 px-2 py-0.5 rounded text-[10px] font-bold",
                                            item.type === "MISMATCH" && "bg-rose-500 text-white",
                                            item.type === "NOT_FOUND" && "bg-rose-500 text-white",
                                            item.type === "MATCHED" && "bg-emerald-500/20 text-emerald-400"
                                        )}>
                                            <span className="material-symbols-outlined text-[12px]">
                                                {item.type === "MATCHED" ? "check_circle" : item.type === "NOT_FOUND" ? "search_off" : "warning"}
                                            </span>
                                            {item.type}
                                        </span>
                                    </div>
                                    <div className="grid grid-cols-2 gap-2 bg-background-dark/40 rounded-lg p-3 border border-slate-700/50">
                                        <div className="border-r border-slate-700/50 pr-2">
                                            <p className="text-[10px] uppercase text-slate-500 font-bold mb-1">Platform Ledger</p>
                                            {item.platformAmount !== null
                                                ? <p className={clsx("text-lg font-bold", isResolved ? "text-slate-400" : "text-slate-100")}>{fmt(item.platformAmount)}</p>
                                                : <p className="text-lg font-bold text-slate-500 italic">Missing</p>
                                            }
                                        </div>
                                        <div className="pl-2">
                                            <p className="text-[10px] uppercase text-slate-500 font-bold mb-1">Yoco API</p>
                                            <p className={clsx("text-lg font-bold", isResolved ? "text-slate-400" : item.type === "MISMATCH" ? "text-rose-400" : "text-slate-100")}>
                                                {item.yocoAmount !== null ? fmt(item.yocoAmount) : "—"}
                                            </p>
                                        </div>
                                    </div>
                                    {!isResolved && (
                                        <div className="flex items-center justify-between pt-1">
                                            <p className="text-[11px] text-slate-400 italic">{item.note}</p>
                                            {item.type === "MISMATCH" && (
                                                <button onClick={() => handleFix(item.id)} className="text-primary text-xs font-bold flex items-center gap-1 hover:underline">
                                                    Fix <span className="material-symbols-outlined text-xs">arrow_forward</span>
                                                </button>
                                            )}
                                            {item.type === "NOT_FOUND" && (
                                                <button onClick={() => handleImport(item.id)} className="text-primary text-xs font-bold flex items-center gap-1 hover:underline">
                                                    Import <span className="material-symbols-outlined text-xs">download</span>
                                                </button>
                                            )}
                                        </div>
                                    )}
                                </div>
                            );
                        })}
                    </div>
                )}

                {/* Overview Tab — Drift Analysis */}
                {activeTab === "Overview" && (
                    <div className="px-4 md:px-0 space-y-4">
                        <h3 className="text-sm font-bold uppercase tracking-widest text-slate-300">Drift Analysis</h3>
                        <div className="w-full bg-slate-800/60 rounded-xl p-5 border border-slate-700/50 relative overflow-hidden">
                            <div
                                className="absolute inset-0 opacity-10 pointer-events-none"
                                style={{ backgroundImage: "radial-gradient(circle at 2px 2px, #135bec 1px, transparent 0)", backgroundSize: "20px 20px" }}
                            />
                            <div className="flex items-end gap-3 h-24 mb-4">
                                {DRIFT_BARS.map((bar, i) => (
                                    <div key={i} className={clsx("flex-1 rounded-t-sm transition-all", bar.color)} style={{ height: bar.h }} />
                                ))}
                            </div>
                            <p className="text-xs text-slate-400 font-medium text-center">Daily reconciliation drift (last 7 days)</p>
                        </div>
                        <div className="grid grid-cols-3 gap-3">
                            {[
                                { label: "Matched", value: items.filter((i) => i.type === "MATCHED").length, color: "text-emerald-400" },
                                { label: "Mismatches", value: items.filter((i) => i.type === "MISMATCH" && !i.resolved).length, color: "text-rose-400" },
                                { label: "Not Found", value: items.filter((i) => i.type === "NOT_FOUND" && !i.resolved).length, color: "text-amber-400" },
                            ].map((stat) => (
                                <div key={stat.label} className="bg-slate-800/60 rounded-xl p-4 border border-slate-700/50 text-center">
                                    <p className={clsx("text-2xl font-bold", stat.color)}>{stat.value}</p>
                                    <p className="text-[10px] text-slate-500 font-medium uppercase tracking-wide mt-1">{stat.label}</p>
                                </div>
                            ))}
                        </div>
                    </div>
                )}
            </main>
        </div>
    );
}

import { RateLimiterMemory } from 'rate-limiter-flexible';

const rateLimiter = new RateLimiterMemory({
    points: 10, // 10 requests
    duration: 60, // Per 60 seconds
});

export async function rateLimit(ip: string) {
    try {
        await rateLimiter.consume(ip);
        return true;
    } catch (rejRes) {
        return false;
    }
}
